using NUnit.Framework;
using Agava.SuffixBigIntegerUtils;
using System;
using System.Numerics;

namespace Agava.SuffixBigInteger.Tests
{
    public class BigIntegerFormatterTests
    {
        [Test]
        public void FormatWithSuffix()
        {
            foreach (string[] data in GetTestDataFromBigIntegerToSuffix())
            {
                BigInteger number = BigInteger.Parse(data[0]);
                string expectedNumericalFormat = data[1];

                Assert.AreEqual(expectedNumericalFormat, BigIntegerExtention.FormatWithSuffix(number));
            }
        }

        [Test]
        public void FromSuffixFormat()
        {
            foreach (string[] data in GetTestDataFromSuffixToBigInteger())
            {
                BigInteger expectedNumber = BigInteger.Parse(data[0]);
                string number = data[1];

                Assert.AreEqual(expectedNumber.ToString(), BigIntegerExtention.ToBigIntegerFromSuffixFormat(number).ToString());
            }

            Assert.AreEqual(1969, BigIntegerExtention.ToBigIntegerFromSuffixFormat("1aaa").ToString().Length);
            Assert.AreEqual(BigInteger.Zero, BigIntegerExtention.ToBigIntegerFromSuffixFormat(".0k"));
        }

        [Test]
        public void ExceptionFromSuffixFormat()
        {
            Assert.Throws<FormatException>(() => { BigIntegerExtention.ToBigIntegerFromSuffixFormat("1.0"); });
            Assert.Throws<FormatException>(() => { BigIntegerExtention.ToBigIntegerFromSuffixFormat("1K"); });
            Assert.Throws<FormatException>(() => { BigIntegerExtention.ToBigIntegerFromSuffixFormat(".0qe3"); });
            Assert.Throws<FormatException>(() => { BigIntegerExtention.ToBigIntegerFromSuffixFormat("abracadabra"); });
            Assert.Throws<FormatException>(() => { BigIntegerExtention.ToBigIntegerFromSuffixFormat("23v2"); });
        }

